import React, { useEffect, useState } from 'react';
import { api } from '../api';
import { saveOfflineArticle } from '../offline';

export default function NewsList({ token }){
  const [category,setCategory]=useState('general');
  const [q,setQ]=useState('');
  const [articles,setArticles]=useState([]);
  const [msg,setMsg]=useState('');

  useEffect(()=>{ fetchNews(); }, [category]);

  async function fetchNews(){
    setMsg('loading...');
    try{
      const res = await api(`/api/news?category=${category}&q=${encodeURIComponent(q)}`);
      setArticles(res.articles || []);
      setMsg('');
    }catch(err){
      setMsg(err.error || 'Failed to fetch');
    }
  }

  async function save(article){
    if(!token){ alert('Login to save.'); return; }
    try{
      await api('/api/bookmarks','POST',{ article }, token);
      alert('Saved to bookmarks');
    }catch(e){ alert(e.error || 'Failed'); }
  }

  async function saveOffline(a){
    try{
      await saveOfflineArticle(a);
      alert('Saved for offline');
    }catch(e){ alert('Failed to save offline'); }
  }

  return (
    <div>
      <h3>Top Headlines</h3>
      <div style={{display:'flex',gap:8, marginBottom:8}}>
        <select value={category} onChange={e=>setCategory(e.target.value)} className='input'>
          <option value='general'>General</option>
          <option value='technology'>Technology</option>
          <option value='business'>Business</option>
          <option value='sports'>Sports</option>
          <option value='health'>Health</option>
          <option value='entertainment'>Entertainment</option>
        </select>
        <input className='input' placeholder='Search' value={q} onChange={e=>setQ(e.target.value)} />
        <button className='button' onClick={fetchNews}>Search</button>
      </div>
      {msg && <div>{msg}</div>}
      {articles.map((a, i)=>(
        <div className='card' key={i}>
          <div style={{display:'flex', justifyContent:'space-between'}}>
            <div style={{fontWeight:600}}>{a.title}</div>
            <div>
              <button className='button' onClick={()=>save(a)}>Save</button>
              <button className='button' onClick={()=>saveOffline(a)} style={{marginLeft:8}}>Save Offline</button>
            </div>
          </div>
          <div style={{marginTop:8}}>{a.description}</div>
          <div style={{marginTop:8}}><a href={a.url} target='_blank' rel='noreferrer'>Read more</a></div>
        </div>
      ))}
    </div>
  );
}
