import React, { useEffect, useState } from 'react';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Signup from './components/Signup';
import NewsList from './components/NewsList';
import Bookmarks from './components/Bookmarks';

function App(){
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('news');
  const [dark, setDark] = useState(false);

  useEffect(()=>{
    if(token) {
      // naive decode to show email if present in token payload (not secure)
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ email:payload.email });
      }catch(e){ setUser(null); }
    } else setUser(null);
  }, [token]);

  function handleLogin(t){
    localStorage.setItem('token', t);
    setToken(t);
    setView('news');
  }
  function handleLogout(){
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
  }

  return (
    <div className={dark ? 'dark' : ''}>
      <div className='app'>
        <Navbar user={user} onLogout={handleLogout} dark={dark} setDark={setDark} />
        <div style={{display:'flex', gap:12}}>
          <div style={{width: '65%'}}>
            {view==='news' && <NewsList token={token} />}
            {view==='bookmarks' && <Bookmarks token={token} />}
          </div>
          <div style={{width:'35%'}}>
            {!token ? <>
              <Login onLogin={handleLogin} />
              <hr style={{margin:'12px 0'}} />
              <Signup onSigned={()=>setView('news')} />
            </> : <div>
              <h4>Actions</h4>
              <button className='button' onClick={()=>setView('news')}>News</button>
              <button className='button' onClick={()=>setView('bookmarks')}>Bookmarks</button>
            </div>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
