import React, { useEffect, useState } from 'react';
import { getOfflineArticles, removeOfflineArticle } from '../offline';

export default function Offline(){
  const [items, setItems] = useState([]);
  const [msg, setMsg] = useState('');

  async function load(){
    setMsg('loading...');
    try{
      const list = await getOfflineArticles();
      setItems(list);
      setMsg(list.length ? '' : 'No offline articles yet');
    }catch(e){ setMsg('Failed to load'); }
  }

  useEffect(()=>{ load(); },[]);

  async function remove(id){
    await removeOfflineArticle(id);
    load();
  }

  return (
    <div>
      <h3>Offline Articles</h3>
      {msg && <div>{msg}</div>}
      {items.map(({ id, article }) => (
        <div className='card' key={id}>
          <div style={{display:'flex', justifyContent:'space-between'}}>
            <div style={{fontWeight:600}}>{article.title}</div>
            <div>
              <a className='button' href={article.url} target='_blank' rel='noreferrer'>Open</a>
              <button className='button' onClick={()=>remove(id)} style={{marginLeft:8}}>Remove</button>
            </div>
          </div>
          <div style={{marginTop:8}}>{article.description}</div>
        </div>
      ))}
    </div>
  );
}
