import React, { useEffect, useState } from 'react';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Signup from './components/Signup';
import NewsList from './components/NewsList';
import Trending from './components/Trending';
import Bookmarks from './components/Bookmarks';
import { enableNotifications, disableNotifications, isSubscribedToPush } from './push';

function App(){
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('news');
  const [dark, setDark] = useState(false);
  const [pushEnabled, setPushEnabled] = useState(false);

  useEffect(()=>{
    if(token) {
      // naive decode to show email if present in token payload (not secure)
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ email:payload.email });
      }catch(e){ setUser(null); }
    } else setUser(null);
  }, [token]);

  useEffect(()=>{
    (async ()=>{
      const s = await isSubscribedToPush();
      setPushEnabled(s);
    })();
  },[]);

  // Sync dark mode class onto <body> so global styles respond correctly
  useEffect(()=>{
    const cls = document.body.classList;
    if (dark) cls.add('dark'); else cls.remove('dark');
  }, [dark]);

  function handleLogin(t){
    localStorage.setItem('token', t);
    setToken(t);
    setView('news');
  }
  function handleLogout(){
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
  }

  return (
    <div>
      <div className='app'>
        <Navbar user={user} onLogout={handleLogout} dark={dark} setDark={setDark}
          pushEnabled={pushEnabled}
          onTogglePush={async()=>{
            try{
              if(pushEnabled){ await disableNotifications(); setPushEnabled(false); }
              else { const ok = await enableNotifications(); setPushEnabled(ok); }
            }catch(e){ alert('Notification setup failed'); }
          }}
        />
        <div style={{display:'flex', gap:12}}>
          <div style={{width: '65%'}}>
            {view==='news' && <NewsList token={token} />}
            {view==='bookmarks' && <Bookmarks token={token} />}
            {view==='trending' && <Trending token={token} />}

          </div>
          <div style={{width:'35%'}} className='side-actions'>
            {!token ? <>
              <Login onLogin={handleLogin} />
              <hr style={{margin:'12px 0'}} />
              <Signup onSigned={()=>setView('news')} />
            </> : <div>
              <h4>Actions</h4>
              <button className='button' onClick={()=>setView('news')}>News</button>
              <button className='button' onClick={()=>setView('bookmarks')}>Bookmarks</button>
              <button className='button' onClick={()=>setView('trending')}>Trending</button>

            </div>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
